"use strict";

var gpii = fluid.registerNamespace("gpii");
var macos = fluid.registerNamespace("gpii.macos");

/**
 * Gets the USB drives that are available.
 *
 * @param {Array<String>} drivePaths [optional] Array of drives (mounting paths) to check. Omit to check all drives on the system.
 * @return {Array<String>} Array of mounting paths to each USB drive.
 */
gpii.macos.getUsbDrives = function (drivePaths) {
    var drives = macos.native.getAllUsbDriveMountPaths();

    // if drivePaths was provided as a filter, remove any drives from our array which are not in the passed-in list
    if (drivePaths) {
        drives = drives.filter(function (diskMountPath) {
            return drivePaths.includes(diskMountPath);
        });    
    }

    return drives;
};

/**
 * Ejects USB drives
 *
 * @param {Array<String>} drivePaths - Array of drives (mounting paths) to eject.
 */
gpii.macos.ejectUsbDrives = function (drivePaths) {
    macos.native.safelyEjectUsbDrives(drivePaths);
}

/**
 * Opens directory paths
 *
 * @param {Array<String>} paths - Array of directory paths to open.
 */
gpii.macos.openDirectories = function (paths) {
    macos.native.openDirectories(paths);
}
