"use strict";

var fluid = require("gpii-universal"),
    gpii = fluid.registerNamespace("gpii"),
    macos = fluid.registerNamespace("gpii.macos");

fluid.registerNamespace("gpii.macos.nativeSettingsHandler");

/**
 * Function that handles calling the native executable for volume control.
 *
 * @param {String} mode The operation mode, could be either "Set" or "Get".
 * @param {Number} [num] The value to set as the current system volume. Range is normalized from 0 to 1.
 * @return {Promise} A promise that resolves on success or holds a object with the error information.
 */
macos.nativeSettingsHandler.VolumeHandler = function (mode, num) {
    var promise = fluid.promise();

    try {
        var value;

        if (mode === "Get") {
            value = macos.native.getAudioVolume();
            //
            // patch for compatibility with Windows (since Windows does not distinguish between "mute" and "volume level")
            var muteState = macos.native.getAudioMuteState();
            if (muteState === true) {
                value = 0.0;
            } 
        } else {
            // out of an abundance of caution, establish floor and ceiling values on "num"
            if (num < 0.0) {
                num = 0.0;
            } else if (num > 1.0) {
                num = 1.0;
            }

            // NOTE: since Windows does not distinguish between "mute" and "volume level", we treat a volume of 0.0 as "mute" for compatibility and best user-experience
            if (num === 0.0) {
                macos.native.setAudioMuteState(true);
            } else {
                // NOTE: there is an edge case of moving to volume "0.0" to indicate an actual volume of 0.0 (instead of "mute"); at this time gpii-app does not provide us enough information to distinguish this condition so we are attempting to provide best-effort here
                macos.native.setAudioVolume(num);
                //
                // patch for compatibility with Windows (since Windows does not distinguish between "mute" and "volume level")...we turn mute off if the user has set a non-zero volume level
                macos.native.setAudioMuteState(false);
            }
            // NOTE: for compatibility with the reference Windows implementation, we return 0.0 here (instead of re-requesting the volume via macos.native.getAudioVolume())
            // option 1 (returning 0.0 to match the Windows implementation)
            value = 0.0;
            // option 2 (returning the actual new volume value)
            // value = macos.native.getAudioVolume();
        }

        promise.resolve(value);
    } catch (err) {
        promise.reject(err);
    }

    return promise;
};

/**
 * Gets the current system volume
 *
 * @return {Promise} A promise that resolves on success or holds a object with the error information.
 */
macos.nativeSettingsHandler.GetVolume = function () {
    return macos.nativeSettingsHandler.VolumeHandler("Get");
};

/**
 * Sets the current system volume
 *
 * @param {Number} num The value to set as the current system volume. Range is normalized from 0 to 1.
 * @return {Promise} A promise that resolves on success or holds a object with the error information.
 */
macos.nativeSettingsHandler.SetVolume = function (num) {
    return macos.nativeSettingsHandler.VolumeHandler("Set", num);
};

macos.nativeSettingsHandler.functions = {
    Volume: {
        set: macos.nativeSettingsHandler.SetVolume,
        get: macos.nativeSettingsHandler.GetVolume
    }
};

/**
 * Helper function that converts a error to string, so the rejection from the
 * settings handler only holds a message.
 *
 * @param {Object} err The error to be stringified.
 * @return {String} The error message.
 */
macos.nativeSettingsHandler.errorInfo = function (err) {
    return "With error - '" + JSON.stringify(err) + "'";
};

/**
 * Helper function for creating a custom message for error rejection.
 *
 * @param {Promise} promise The promise to be rejected.
 * @param {String} settingKey The name of the requested setting for which an operation failed.
 * @param {Object} err The error object.
 * @param {String} message A message with information about the operation.
 */
macos.nativeSettingsHandler.reject = function (promise, settingKey, err, message) {
    var operation = message + settingKey + "' failed. ";
    var errMsg = "nativeSettingsHandler" + operation + macos.nativeSettingsHandler.errorInfo(err);

    promise.reject(errMsg);
};

/**
 * Setter for the nativeSettingsHandler.
 *
 * The payload should have the following format:
 *
 * {
 *      "functionName": "FunctionName",
 *      "setParam": val
 * }
 *
 * The first two items:
 *   - function: Function name for the function to be used to set/get the value.
 *   - setParam: Parameter to be passed to the 'set' function.
 *
 * @param {Object} payload The payload.
 * @return {Promise} Resolves with the response.
 */
macos.nativeSettingsHandler.setImpl = function (payload) {
    var pRes = fluid.promise();

    var fnName = fluid.get(payload, "options.functionName");
    var setFn = fluid.get(macos.nativeSettingsHandler.functions, fnName + ".set");
    var getFn = fluid.get(macos.nativeSettingsHandler.functions, fnName + ".get");
    var fnNotDef = [": Failed due to '", " " + fnName + "' function not being defined."];

    if (setFn === undefined) {
        pRes.reject("nativeSettingsHandler" + fnNotDef[0] + "set" + fnNotDef[1]);
    } else if (getFn === undefined) {
        pRes.reject("nativeSettingsHandler" + fnNotDef[0] + "get" + fnNotDef[1]);
    } else {
        var results = {};
        var settingsArray = fluid.makeArray(payload.settings);

        if (settingsArray.length === 0) {
            pRes.reject("nativeSettingsHandler: Failed due to empty payload.");
        } else {
            var uniqueSetting = settingsArray[0];
            // Get the payload value to set.
            var settingKey = fluid.keys(uniqueSetting)[0];
            var valueToSet = fluid.values(uniqueSetting)[0].value;

            // Create object for storing setting results.
            results[settingKey] = {};
            // Get the current values for the setting.
            var pGetOld = getFn();

            pGetOld.then(
                function (oldVal) {
                    // Store the current value in results.
                    fluid.set(results[settingKey], "oldValue.value", oldVal);

                    var pSet = setFn(valueToSet);

                    pSet.then(
                        function () {
                            var pGetNew = getFn();

                            pGetNew.then(
                                function (newVal) {
                                    fluid.set(results[settingKey], "newValue.value", newVal);

                                    pRes.resolve(results);
                                },
                                function (err) {
                                    macos.nativeSettingsHandler.reject(pRes, settingKey, err, ": Getting new value for setting '");
                                }
                            );
                        },
                        function (err) {
                            macos.nativeSettingsHandler.reject(pRes, settingKey, err, ": Setting new value for setting '");
                        }
                    );
                },
                function (err) {
                    macos.nativeSettingsHandler.reject(pRes, settingKey, err, ": Getting current value for setting '");
                }
            );
        }
    }

    return pRes;
};

/**
 * Getter for the nativeSettingsHandler.
 *
 * @param {Object} payload The payload.
 * @return {Promise} Resolves with the response.
 */
macos.nativeSettingsHandler.getImpl = function (payload) {
    var pRes = fluid.promise();

    var fnName = fluid.get(payload, "options.functionName");
    var getFn = fluid.get(macos.nativeSettingsHandler.functions, fnName + ".get");
    var fnNotDef = [": Failed due to '", " " + fnName + "' function not being defined."];

    if (getFn === undefined) {
        pRes.reject("nativeSettingsHandler" + fnNotDef[0] + "get" + fnNotDef[1]);
    } else {
        var settingsArray = fluid.makeArray(payload.settings);

        if (settingsArray.length === 0) {
            pRes.reject("nativeSettingsHandler: Failed due to empty payload.");
        } else {
            var uniqueSetting = settingsArray[0];
            var results = {};

            // Get payload setting and prepare results for returning it
            var settingKey = fluid.keys(uniqueSetting)[0];
            results[settingKey] = {};

            var pGetRes = getFn();

            pGetRes.then(
                function (curVal) {
                    results[settingKey].value = curVal;

                    pRes.resolve(results);
                },
                function (err) {
                    macos.nativeSettingsHandler.reject(pRes, settingKey, err, ": Getting current value for setting '");
                }
            );
        }
    }

    return pRes;
};

/**
 * Invoke the settings handler.
 *
 * @param {Object} payload The payload
 * @return {Promise} Resolves with the response.
 */
macos.nativeSettingsHandler.get = function (payload) {
    return gpii.settingsHandlers.invokeSettingsHandler(macos.nativeSettingsHandler.getImpl, payload);
};

/**
 * Invoke the settings handler.
 *
 * @param {Object} payload The payload
 * @return {Promise} Resolves with the response.
 */
macos.nativeSettingsHandler.set = function (payload) {
    return gpii.settingsHandlers.invokeSettingsHandler(macos.nativeSettingsHandler.setImpl, payload);
};
